mapper.select:
select * from reply 
	where rp_valid = 'I' and rp_bd_num = #{num}
	order by rp_num desc
	limit #{cri.pageStart},#{cri.perPageNum}

mapper.update:
update reply set
	rp_content = #{reply.rp_content},
	rp_upDate = now(),
	rp_valid = #{reply.rp_valid}
	where rp_valid = 'I' and rp_num = #{reply.rp_num}
