mapper.select:
select * from board 
	where valid = 'I' and type = #{type}
	order by oriNo desc, groupOrd asc
	limit #{pageStart}, #{perPageNum}

mapper.insert:
insert into board(title, contents, writer, type, pw, oriNo, groupOrd) select #{board.title}, #{board.contents}, #{board.writer}, #{board.type}, #{board.pw},
#{board.oriNo}, ifnull(max(groupOrd)+1,1) from board where oriNo = #{board.oriNo}
ifnull(max(num)+1,1), 0 from board

mapper.update:
update board set
	title = #{title},
	contents = #{contents}
	where num = #{num}

update board set
	valid = 'D'
	where num = #{num}

update board set
	valid = 'D'
	where oriNo = #{num}

update board set
	views = views+1
	where num = #{num}

update file set
	state = 'D'
	where num = #{num}
